import React, { useEffect, useState } from "react";
import { json } from "../../db";
const FeedBackContent = () => {
  const [source, setSource] = useState("");
  const [destination, setDestination] = useState("");
  const [travelDate, setTravelDate] = useState("");
  const [safetyMeasures, setSafetyMeasures] = useState([]);
  const [noSafetyMeasures, setNoSafetyMeasures] = useState({});

  useEffect(() => {
    const {
      source,
      destination,
      traveldate,
      safetyMeasures,
      noSafetyMeasures,
    } = json;

    setSource(source);
    setDestination(destination);
    setSafetyMeasures(safetyMeasures);
    setNoSafetyMeasures(noSafetyMeasures);
    setTravelDate(traveldate);
  }, []);

  const getMonthName = (years) => {
    const yearsName = [
      "jan",
      "feb",
      "mar",
      "apr",
      "may",
      "jun",
      "jul",
      "aug",
      "sep",
      "oct",
      "nov",
      "dec",
    ];
    return yearsName[years - 1];
  };
  return (
    <main>
      <h3>Help us make travel safer</h3>
      <p>
        Select safety measures followed on your trip from{" "}
        <strong>{source}</strong> to{" "}
        <strong>
          {destination} on{" "}
          {travelDate.getDate() + "th" + getMonthName(travelDate.getMonth())}
        </strong>
      </p>
    </main>
  );
};

export default FeedBackContent;
